const fs = require('fs');

exports.getTitles = function(file_json, file_tmpl, res) {
    fs.readFile(file_json, (err, data) => {
        if (err) return hadError(err, res);
        getTemplate(file_tmpl, JSON.parse(data.toString()), res);
    });
}

function getTemplate(file_tmpl, titles, res) {
    fs.readFile(file_tmpl, (err, data) => {
        if (err) return hadError(err, res);
        formatHtml(titles, data.toString(), res);
    });
}

function formatHtml(titles, tmpl, res) {
    const html = tmpl.replace('%', titles.join('</li><li>'));
    res.writeHead(200, {'Content-Type': 'text/html'});
    res.end(html);
}

function hadError(err, res) {
    console.error(err);
    res.end('Server Error');
}